package ar.edu.unlam.tallerweb1.domain.archivos;

import ar.edu.unlam.tallerweb1.model.Imagen;
import ar.edu.unlam.tallerweb1.model.Publicacion;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;
import org.springframework.web.multipart.MultipartFile;

import java.io.IOException;
import java.util.Base64;

@Service
public class ServicioArchivo implements IServicioArchivo {


    private final String defaultUserImageEncoded = "";
    private final String defaultMascotImageEncoded = "";
    private IRepositorioArchivo repositorioArchivo;



    @Autowired
    public ServicioArchivo(IRepositorioArchivo repositorioArchivo) {
        this.repositorioArchivo = repositorioArchivo;
    }

    @Override
    public String encodeImage(MultipartFile multipart) {

        try {
            return !multipart.isEmpty()? this.bytesToBase64(multipart.getBytes()) : null;
        }catch (IOException ex){
            System.out.println("Error: " + ex.getMessage());
            return null;
        }


    }


    @Override
    public String getDefaultUserImageEncoded() {
        return this.defaultUserImageEncoded;
    }

    @Override
    public String getDefaultMascotImageEncoded() {
        return this.defaultMascotImageEncoded;
    }

    @Override
    public int subirImagenesPost(MultipartFile[] files, Publicacion post) {

        int subidos = 0;

        // TODO if(files.length > 4) ;

        for (MultipartFile file : files) {

            try {
                this.repositorioArchivo.registrarImagen(new Imagen(this.bytesToBase64(file.getBytes()), post));
                subidos++;
            }catch (IOException ex){
                System.out.println("Error: " + ex.getMessage());
            }

        }

        return subidos;
    }


    @Override
    public boolean eliminarImagenPost(Long idImagen){
        Imagen img = new Imagen();
        img.setId(idImagen);

        this.repositorioArchivo.eliminarImagen(img);
        return true;

    }



    private String bytesToBase64(byte[] bytes)throws IOException {
        String encodedString = Base64.getEncoder().encodeToString(bytes);
        return encodedString;
    }




}
